import React, { useState } from 'react';

const PostForm = (props) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [location, setLocation] = useState('');
    const [willDeliver, setWillDeliver] = useState(false);

    const handleSubmit = async (event) => {

        event.preventDefault();

        const locallySourcedToken = localStorage.getItem("token");

        const response = await fetch(`${BASE_URL}/`, {
            method: "POST",
            headers: {
                'Content-Type': "application/json",
                'Authorization': `Bearer ${locallySourcedToken}`
            },
            body: JSON.stringify({
                post: {
                    title: title,
                    description: description,
                    priceOfItem: price,
                    location: location,
                    willDeliver: willDeliver
                }
            })
        })
        const data = response.json();

    }

    return (
        <div>
            <form>
                <input type="text" value={title} placeholder="Title">

                </input>
                <textarea type="text" value={description} placeholder="Description">

                </textarea>
                <input type="text" value={price} placeholder="Price">

                </input>
                <input type="text" value={location} placeholder="Location">

                </input>
                <input type="checkbox" value={willDeliver}>

                </input>
            </form>
        </div>
    )
}

export default PostForm;
